class CacheStorage{constructor(){this.cache=new Map,this.storageName="CacheStorage",this.load()}get(a,b){b=b||{expired:86400000};try{const c=this.cache.get(a);if(!c)return;const d=new Date,e=new Date(c.updated_at);if(d-e>=b.expired)throw new Error(`Item is expired ${a}`);return c.value}catch(a){}}add(a,b){this.cache.set(a,{value:b,updated_at:new Date().toISOString()}),this.save()}load(){const a=localStorage.getItem(this.storageName);a&&(this.cache=new Map(Object.entries(JSON.parse(a))))}save(){const a=JSON.stringify(Object.fromEntries(this.cache));localStorage.setItem(this.storageName,a)}}const LocalCache=new CacheStorage;